import java.math.*;
public class largestProductSeries {
	public static void main(String[] args) {
		String array[] = new String[1000];
		String str = "";
		array = str.split("");
		int intArray[] = convertToInt(array);
		System.out.println(lPS(intArray));
	}
	public static BigInteger lPS(int[] array){
		BigInteger lps = new BigInteger("0");
		BigInteger tempNum = new BigInteger("1");
		for(int i = 0;i<array.length-13;i++){
			tempNum = BigInteger.valueOf(1);
			for(int j = i;j<i+13;j++){
				tempNum= tempNum.multiply(BigInteger.valueOf(array[j]));
			}
			if(tempNum.compareTo(lps) == 1){
				lps = tempNum;
			}
		}
		return lps;
	}
	public static int[] convertToInt(String[] stringArray){
		int intArray[] = new int[stringArray.length];
		for(int i = 0;i<intArray.length;i++){
			intArray[i] = Integer.parseInt(stringArray[i]);
		}
		return intArray;
	}
}
